subsection.source:
DefaultOptionBuilder oBuilder = new DefaultOptionBuilder(); ArgumentBuilder aBuilder = new ArgumentBuilder(); GroupBuilder gBuilder = new GroupBuilder();
DefaultOption xmlOption = oBuilder .withLongName("xml") .withDescription("Output using xml format") .create();
Argument pathArgument = aBuilder .withName("path") .withMinimum(1) .withMaximum(1) .create();
Group outputChildren = gBuilder .withOption(xmlOption) .create();
Option outputOption = oBuilder .withLongName("output") .withDescription("Outputs to a file") .withArgument(pathArgument) .withChildren(outputChildren) .create();

// configure the options
Group options = ...;

// configure a HelpFormatter
HelpFormatter hf = new HelpFormatter();

// configure a parser
Parser p = new Parser();
p.setGroup(options);
p.setHelpFormatter(hf);
p.setHelpTrigger("--help");
CommandLine cl = p.parseAndHelp(new String[]{});

// abort application if no CommandLine was parsed
if(cl==null) {
    System.exit(-1);
}

// if we have --output option
if(cl.hasOption("--output")) {
    // grab the path
    String path = (String)cl.getValue("--output");
    // grab the format
    boolean xml = cl.hasOption("--xml");
    // configure the application's output
    configureOutput(path,xml);
}
